mapper.update:
update t_user
userId=#{userId}, identityCard=#{identityCard}, name=#{name}, phone=#{phone}, email=#{email}, password=#{password}, avatar=#{avatar}, smallAvatar=#{smallAvatar}, provinceId=#{provinceId}, cityId=#{cityId}, registerTime=#{registerTime}, updateTime=#{updateTime}, active=#{active}, addr=#{addr}, clientId=#{clientId}
where id = #{id} AND status=#{status}

mapper.select:
SELECT count(1) from t_user

	id = #{id}
	AND userId=#{userId}
	AND identityCard=#{identityCard}
	AND name LIKE CONCAT('%',#{name},'%')
	AND phone=#{phone}
	AND email=#{email}
	AND password=#{password}
	AND avatar=#{avatar}
	AND smallAvatar=#{smallAvatar}
	AND provinceId=#{provinceId}
	AND cityId=#{cityId}
	AND registerTime=#{registerTime}
	AND updateTime=#{updateTime}
	AND active=#{active}
	AND status=#{status}
	AND addr=#{addr}
	AND clientId=#{clientId}

		SELECT 
        	t.id AS id,
        	t.userId AS userId,
        	t.identityCard AS identityCard,
        	t.name AS name,
        	t.phone AS phone,
        	t.email AS email,
        	t.password AS password,
        	t.avatar AS avatar,
        	t.smallAvatar AS smallAvatar,
        	t.provinceId AS provinceId,
        	t.cityId AS cityId,
        	t.registerTime AS registerTime,
        	t.updateTime AS updateTime,
        	t.active AS active,
        	t.status AS status,
        	t.addr AS addr,
        	t.clientId AS clientId
        FROM
        	t_user t

			t.id = #{id}
			AND t.userId=#{userId}
			AND t.identityCard=#{identityCard}
			AND t.name LIKE CONCAT('%',#{name},'%')
			AND t.phone=#{phone}
			AND t.email=#{email}
			AND t.password=#{password}
			AND t.avatar=#{avatar}
			AND t.smallAvatar=#{smallAvatar}
			AND t.provinceId=#{provinceId}
			AND t.cityId=#{cityId}
			AND t.registerTime=#{registerTime}
			AND t.updateTime=#{updateTime}
			AND t.active=#{active}
			AND t.status=#{status}
			AND t.addr=#{addr}
			AND clientId=#{clientId}

		order by t.id asc

mapper.update:
update t_user
active=3 active=1
where id = #{userId}

update t_user

	password=#{password},
	updateTime=#{updateTime}

 where phone = #{phone}
  AND status=#{status}
