mapper.update:
update T_COMMON_REGION
AREA_CODE=#{areaCode}, AREA_NAME=#{areaName}, AREA_TYPE=#{areaType}, REMARK=#{remark}, AREA_ENABLED=#{areaEnabled}, LEVEL_ID=#{levelId}
where ID = #{id}

mapper.select:
		SELECT count(1) from T_COMMON_REGION
		
			ID = #{id}
			 AND AREA_CODE like #{areaCode} || '%'
			 AND AREA_NAME like '%' || #{areaName} || '%'
			 AND AREA_TYPE = #{areaType}
			 AND REMARK = #{remark}
			 AND AREA_ENABLED = #{areaEnabled}
			 AND LEVEL_ID = #{levelId}

		SELECT 
        	t.ID AS id,
        	t.AREA_CODE AS areaCode,
        	t.AREA_NAME AS areaName,
        	t.AREA_TYPE AS areaType,
        	t.REMARK AS remark,
        	t.AREA_ENABLED AS areaEnabled,
        	t.LEVEL_ID AS levelId
        FROM
        	T_COMMON_REGION t
        	
			ID = #{id}
			 AND AREA_CODE like #{areaCode} || '%'
			 AND AREA_NAME = #{areaName}
			 AND AREA_TYPE = #{areaType}
			 AND REMARK = #{remark}
			 AND AREA_ENABLED = #{areaEnabled}
			 AND LEVEL_ID = #{levelId}
