mapper.update:
update SY0019
MSG_ID=#{msgId}, RECEVIE_USER_ID=#{recevieUserId}, SEND_USER_ID=#{sendUserId}, STATE=#{state}, OPERATE_TIME=#{operateTime}
where ID = #{id}

mapper.select:
		SELECT count(1) from SY0019
		
			ID = #{id}
			 AND MSG_ID = #{msgId}
			 AND RECEVIE_USER_ID = #{recevieUserId}
			 AND SEND_USER_ID = #{sendUserId}
			 AND STATE = #{state}
			 AND OPERATE_TIME >= #{operateTimeBefore}
			 AND OPERATE_TIME <= #{operateTimeAfter}

		SELECT 
        	t.ID AS id,
        	t.MSG_ID AS msgId,
        	t.RECEVIE_USER_ID AS recevieUserId,
        	t.SEND_USER_ID AS sendUserId,
        	t.STATE AS state,
        	t.OPERATE_TIME AS operateTime
        FROM
        	SY0019 t
        	
			ID = #{id}
			 AND MSG_ID = #{msgId}
			 AND RECEVIE_USER_ID = #{recevieUserId}
			 AND SEND_USER_ID = #{sendUserId}
			 AND STATE = #{state}
			 AND OPERATE_TIME >= #{operateTimeBefore}
			 AND OPERATE_TIME <= #{operateTimeAfter}

		
		
			ORDER BY ${sort} ${order}
