mapper.update:
update manager
mname=#{mname}, pswd=#{pswd}, realname=#{realname}, sex=#{sex}, age=#{age}, tel=#{tel}, entitle=#{entitle}
where id = #{id}

mapper.select:
SELECT count(1) from manager

	id = #{id}
	 AND mname = #{mname}
	 AND pswd = #{pswd}
	 AND realname = #{realname}
	 AND sex = #{sex}
	 AND age = #{age}
	 AND tel = #{tel}
	 AND entitle = #{entitle}

		SELECT 
        	t.id AS id,
        	t.mname AS mname,
        	t.pswd AS pswd,
        	t.realname AS realname,
        	t.sex AS sex,
        	t.age AS age,
        	t.tel AS tel,
        	t.entitle AS entitle
        FROM
        	manager t

			id = #{id}
			 AND mname = #{mname}
			 AND pswd = #{pswd}
			 AND realname = #{realname}
			 AND sex = #{sex}
			 AND age = #{age}
			 AND tel = #{tel}
			 AND entitle = #{entitle}

				 AND t.COMMAND_FILE_ID IN (

  						${item}

				 )




			ORDER BY ${sort} ${order}
