mapper.update:
update review
ip=#{ip}, sj=#{sj}, username=#{username}, homepage=#{homepage}, face=#{face}, title=#{title}, content=#{content}
where id = #{id}

mapper.select:
SELECT count(1) from review

	id = #{id}
	 AND ip = #{ip}
	 AND sj >= #{sjBefore}
	 AND sj <= #{sjAfter}
	 AND username = #{username}
	 AND homepage = #{homepage}
	 AND face = #{face}
	 AND title = #{title}
	 AND content = #{content}

		SELECT 
        	t.id AS id,
        	t.ip AS ip,
        	t.sj AS sj,
        	t.username AS username,
        	t.homepage AS homepage,
        	t.face AS face,
        	t.title AS title,
        	t.content AS content
        FROM
        	review t

			id = #{id}
			 AND ip = #{ip}
			 AND sj >= #{sjBefore}
			 AND sj <= #{sjAfter}
			 AND username = #{username}
			 AND homepage = #{homepage}
			 AND face = #{face}
			 AND title = #{title}
			 AND content = #{content}

				 AND t.COMMAND_FILE_ID IN (

  						${item}

				 )




			ORDER BY ${sort} ${order}
