mapper.update:
update guanggao

	title=#{title},
	zuozhe=#{zuozhe},
	content=#{content},
	fabu_time=#{fabuTime}


where id = #{id}

mapper.select:
SELECT count(1) from guanggao

	id = #{id}
	 AND title = #{title}
	 AND zuozhe = #{zuozhe}
	 AND content = #{content}
	 AND fabu_time >= #{fabuTimeBefore}
	 AND fabu_time <= #{fabuTimeAfter}

		SELECT 
        	t.id AS id,
        	t.title AS title,
        	t.zuozhe AS zuozhe,
        	t.content AS content,
        	t.fabu_time AS fabuTime
        FROM
        	guanggao t

			id = #{id}
			 AND title = #{title}
			 AND zuozhe = #{zuozhe}
			 AND content = #{content}
			 AND fabu_time >= #{fabuTimeBefore}
			 AND fabu_time <= #{fabuTimeAfter}

				 AND t.COMMAND_FILE_ID IN (

  						${item}

				 )




			ORDER BY ${sort} ${order}
