mapper.update:
update staff
name=#{name}, country=#{country}, age=#{age}, sex=#{sex}, career=#{career}, pname=#{pname}
where id = #{id}

mapper.select:
SELECT count(1) from staff

	id = #{id}
	 AND name = #{name}
	 AND country = #{country}
	 AND age = #{age}
	 AND sex = #{sex}
	 AND career = #{career}
	 AND pname = #{pname}

		SELECT 
        	t.id AS id,
        	t.name AS name,
        	t.country AS country,
        	t.age AS age,
        	t.sex AS sex,
        	t.career AS career,
        	t.pname AS pname
        FROM
        	staff t

			id = #{id}
			 AND name = #{name}
			 AND country = #{country}
			 AND age = #{age}
			 AND sex = #{sex}
			 AND career = #{career}
			 AND pname = #{pname}

				 AND t.COMMAND_FILE_ID IN (

  						${item}

				 )




			ORDER BY ${sort} ${order}
