mapper.update:
update user
username=#{username}, password=#{password}, realname=#{realname}, sex=#{sex}, age=#{age}, tel=#{tel}
where id = #{id}

mapper.select:
SELECT count(1) from user

	id = #{id}
	 AND username = #{username}
	 AND password = #{password}
	 AND realname = #{realname}
	 AND sex = #{sex}
	 AND age = #{age}
	 AND tel = #{tel}

		SELECT 
        	t.id AS id,
        	t.username AS username,
        	t.password AS password,
        	t.realname AS realname,
        	t.sex AS sex,
        	t.age AS age,
        	t.tel AS tel
        FROM
        	user t

			id = #{id}
			 AND username = #{username}
			 AND password = #{password}
			 AND realname = #{realname}
			 AND sex = #{sex}
			 AND age = #{age}
			 AND tel = #{tel}

				 AND t.COMMAND_FILE_ID IN (

  						${item}

				 )




			ORDER BY ${sort} ${order}
